export function Download() {
  const { ref, isVisible } = useScrollAnimation<HTMLElement>({ threshold: 0.2 });
  const isMobile = useMediaQuery('(max-width: 768px)');
  const t = useTranslation();
  
  const hasWindowsDownload = DOWNLOAD_URLS.windows.trim().length > 0;
  const hasMacDownload = DOWNLOAD_URLS.mac.trim().length > 0;
  const hasLinuxDownload = DOWNLOAD_URLS.linux.trim().length > 0;
  const hasCoffeeLink = GUMROAD_COFFEE_URL.trim().length > 0;
  
  return (
    <section 
      id="download"
      ref={ref}
      className="landing-section"
      style={{
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0) scale(1)' : 'translateY(30px) scale(0.95)',
        transition: 'opacity 0.6s ease, transform 0.6s ease',
      }}
    >
      <Stack gap={isMobile ? "lg" : "xl"} align="center">
        <Box ta="center" mb={isMobile ? "sm" : "lg"}>
          <Text size="sm" c="forge.4" fw={500} mb="xs">
            {t.download.label}
          </Text>
          <Title order={2} size={isMobile ? "1.5rem" : undefined} c="white">
            {t.download.title}
          </Title>
          <Text size={isMobile ? "sm" : "md"} c="dimmed" mt="xs">
            {t.download.subtitle}
          </Text>
        </Box>
        
        <Box className="pricing-card" maw={500} w="100%">
          <Stack gap={isMobile ? "md" : "lg"} align="center">
            <Badge 
              size={isMobile ? "lg" : "xl"}
              variant="light" 
              color="forge"
              style={{ 
                boxShadow: '0 0 15px rgba(31, 174, 122, 0.3)',
                fontSize: isMobile ? '1rem' : '1.2rem',
                padding: '12px 24px',
              }}
            >
              {t.download.badge}
            </Badge>
            
            {/* Download buttons */}
            <Stack gap="sm" w="100%">
              <Button
                size={isMobile ? "md" : "lg"}
                fullWidth
                className="cta-primary"
                component="a"
                href={hasWindowsDownload ? DOWNLOAD_URLS.windows : '#download'}
                target={hasWindowsDownload ? '_blank' : undefined}
                rel={hasWindowsDownload ? 'noopener noreferrer' : undefined}
                disabled={!hasWindowsDownload}
                leftSection={<WindowsIcon />}
                rightSection={<DownloadIcon />}
                styles={{
                  root: {
                    height: isMobile ? 48 : 56,
                  }
                }}
              >
                {t.download.windows}
              </Button>
              
              <Group grow gap="sm">
                <Button
                  size={isMobile ? "sm" : "md"}
                  variant="light"
                  color="forge"
                  component="a"
                  href={hasMacDownload ? DOWNLOAD_URLS.mac : '#download'}
                  target={hasMacDownload ? '_blank' : undefined}
                  rel={hasMacDownload ? 'noopener noreferrer' : undefined}
                  disabled={!hasMacDownload}
                  leftSection={<AppleIcon />}
                >
                  {t.download.mac}
                </Button>
                
                <Button
                  size={isMobile ? "sm" : "md"}
                  variant="light"
                  color="forge"
                  component="a"
                  href={hasLinuxDownload ? DOWNLOAD_URLS.linux : '#download'}
                  target={hasLinuxDownload ? '_blank' : undefined}
                  rel={hasLinuxDownload ? 'noopener noreferrer' : undefined}
                  disabled={!hasLinuxDownload}
                  leftSection={<LinuxIcon />}
                >
                  {t.download.linux}
                </Button>
              </Group>
            </Stack>
            
            {/* Installation notes accordion */}
            <Accordion
              variant="filled"
              w="100%"
              styles={{
                item: {
                  backgroundColor: 'rgba(15, 46, 38, 0.3)',
                  borderColor: 'rgba(31, 174, 122, 0.15)',
                  borderRadius: 8,
                },
                control: {
                  padding: '8px 12px',
                  '&:hover': {
                    backgroundColor: 'rgba(31, 174, 122, 0.1)',
                  },
                },
                label: {
                  color: 'var(--mantine-color-dimmed)',
                  fontSize: '0.75rem',
                },
                content: {
                  padding: '0 12px 12px',
                },
                chevron: {
                  color: '#1FAE7A',
                },
              }}
            >
              <Accordion.Item value="install-notes">
                <Accordion.Control icon={<InfoIcon />}>
                  {t.download.installTitle}
                </Accordion.Control>
                <Accordion.Panel>
                  <Stack gap="xs">
                    <Group gap="xs" align="flex-start">
                      <Box style={{ flexShrink: 0, marginTop: 2 }}>
                        <WindowsIcon />
                      </Box>
                      <Text size="xs" c="dimmed">
                        {t.download.windowsNote}
                      </Text>
                    </Group>
                    <Group gap="xs" align="flex-start">
                      <Box style={{ flexShrink: 0, marginTop: 2 }}>
                        <AppleIcon />
                      </Box>
                      <Text size="xs" c="dimmed">
                        {t.download.macNote}
                      </Text>
                    </Group>
                  </Stack>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
            
            <Text size="xs" c="dimmed" ta="center">
              {t.download.note}
            </Text>
            
            <Divider 
              w="100%" 
              my="sm" 
              color="rgba(31, 174, 122, 0.2)"
              label={
                <Text size="xs" c="dimmed">{t.download.supportTitle}</Text>
              }
              labelPosition="center"
            />
            
            {/* Support section */}
            <Stack gap="xs" w="100%">
              <Text size="sm" c="dimmed" ta="center">
                {t.download.supportDesc}
              </Text>
              
              <Group justify="center" gap="md" mt="xs">
                <Anchor
                  href={INSTAGRAM_URL}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{ textDecoration: 'none' }}
                >
                  <Button
                    size="sm"
                    variant="subtle"
                    color="forge"
                    leftSection={<InstagramIcon />}
                  >
                    {t.download.instagram}
                  </Button>
                </Anchor>
                
                <Anchor
                  href={ETSY_URL}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{ textDecoration: 'none' }}
                >
                  <Button
                    size="sm"
                    variant="subtle"
                    color="forge"
                    leftSection={<EtsyIcon />}
                  >
                    {t.download.etsy}
                  </Button>
                </Anchor>
                
                {hasCoffeeLink && (
                  <Anchor
                    href={GUMROAD_COFFEE_URL}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{ textDecoration: 'none' }}
                  >
                    <Button
                      size="sm"
                      variant="subtle"
                      color="forge"
                      leftSection={<CoffeeIcon />}
                    >
                      {t.download.coffee}
                    </Button>
                  </Anchor>
                )}
              </Group>
            </Stack>
          </Stack>
        </Box>
      </Stack>
    </section>
  );
}
